 <!-- Modal Avtokran -->
 <div
        class="modal fade"
        id="avtokranModal"
        tabindex="-1"
        role="dialog"
        aria-labelledby="avtokranModalLabel"
        aria-hidden="true"
      >
        <div class="modal-dialog" role="document">
          <div class="modal-content">
            <div class="modal-header">
              <h5 class="modal-title" id="avtokranModalLabel">Автокран</h5>
              <button
                type="button"
                class="close"
                data-dismiss="modal"
                aria-label="Close"
              >
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div class="modal-body">
              <ul>
                <li>- 12,5 тона;</li>
                <li>- 16 м. дължина на стрелата;</li>
              </ul>
            </div>
            <div class="modal-footer">
              <button
                type="button"
                class="btn btn-secondary"
                data-dismiss="modal"
              >
                Затвори
              </button>
            </div>
          </div>
        </div>
      </div>

